import React, { useState } from "react";
function App() {
  let x = "none";
  const [myX, setX] = useState(x);
  const ToggleShow = function () {
    setX((prevousState) => {
      return prevousState === "none" ? "block" : "none";
    });
  };
  return (
    <div>
      <button onClick={ToggleShow}>Show / hide</button>
      <div style={{ display: myX }}>box</div>
    </div>
  );
}

export default App;
