import React from "react";
import { useState, useRef } from "react";
import "./App.css";
import styled from "styled-components";
const Img = styled.img`
  width: 400px;
`;
function App() {
  const image1Ref = useRef();
  const image2Ref = useRef();

  const image1 = require("./img/image1.jpg");
  const image1BlackWhite = require("./img/image1Bw.jpg");

  const image2 = require("./img/image2.jpg");
  const image2BlackWhite = require("./img/image2Bw.jpg");

  return (
    <div>
      <Img
        onMouseOver={() => console.log(image1Ref.current.src)}
        ref={image1Ref}
        src={image1}
      ></Img>
      <Img ref={image2Ref} src={image2}></Img>
    </div>
  );
}

export default App;
