import React from "react";
import { useState, useRef } from "react";
import "./App.css";
import styled from "styled-components";
const Img = styled.img`
  width: 400px;
`;
function App() {
  const image1 = require("./img/image1.jpg");
  const image2 = require("./img/image2.jpg");

  const image2BlackWhite = require("./img/image1Bw.jpg");
  const image1BlackWhite = require("./img/image2Bw.jpg");

  const image1Ref = useRef(image1);
  const image2Ref = useRef(image2);

  const changeImage = function (e) {
    console.log(e.current);
  };

  return (
    <div>
      <Img
        // onMouseEnter={() => (image1Ref.current.src = image1BlackWhite)}
        // onMouseLeave={() => (image1Ref.current.src = image1)}
        onMouseLeave={changeImage}
        ref={image1Ref}
        src={image1}
      ></Img>
      <Img
        onMouseEnter={() => (image2Ref.current.src = image2BlackWhite)}
        onMouseLeave={() => (image2Ref.current.src = image2)}
        ref={image2Ref}
        src={image2}
      ></Img>
    </div>
  );
}

export default App;
