import { useState } from "react";

function Counter() {
  let myColor = "black";
  let [currColor, setColor] = useState(myColor);
  let count = 0;
  let [num, setNum] = useState(count);
  const dec = function () {
    if (num > -10) {
      setNum((previousState) => previousState - 1);
      if (num < 0)
        setColor((previousState) => {
          console.log(previousState);
          return "red";
        });
    }
  };

  const inc = function () {
    if (num < 10) {
      setNum((previousState) => previousState + 1);
      if (num > 0)
        setColor((previousState) => {
          console.log(previousState);
          return "green";
        });
    }
  };

  return (
    <div>
      <button onClick={dec}>-</button>
      <label style={{ backgroundColor: { currColor } }}>{num}</label>
      <button onClick={inc}>+</button>
    </div>
  );
}
export default Counter;
