import { useState } from "react";
import "./form.css";
function Form() {
  const [text, setText] = useState("");
  const saveChangeHandler = function (event) {};
  console.log(text);

  return (
    <form>
      <container>
        <label for="read">First Name</label>
        <input
          type="text"
          id="firstName"
          onChange={(e) => setText(e.target.id)}
        />
      </container>
      <container>
        <label for="accept">Last Name</label>
        <input
          type="text"
          id="lastName"
          onChange={(e) => setText(e.target.id)}
        />
      </container>
      <container>
        <label for="want1">Age</label>
        <select
          type="number"
          id="age"
          checked
          onChange={(e) => setText((prev) => e.target.id)}
        />
      </container>
      <container>
        <label for="freeText">Free Text</label>
        <input
          type="textarea"
          id="freeText"
          checked
          onChange={(e) => setText(e.target.id)}
        />
      </container>
      <button>Submit</button>
    </form>
  );
}

export default Form;
