import { useState } from "react";
import "./form.css";
function Form() {
  const [data, setTsetDataext] = useState({
    firstName: "",
    lastName: "",
    age: "",
    freeText: "",
  });
  const changeHandler = function (event) {
    const id = event.target.id;
    setTsetDataext((existingValues) => ({ ...data, firstName: id }));
    console.log(data);
  };
  return (
    <form>
      <container>
        <label for="read">First Name</label>
        <input type="text" id="firstName" onChange={changeHandler} />
      </container>
      <container>
        <label for="accept">Last Name</label>
        <input type="text" id="lastName" />
      </container>
      <container>
        <label for="want1">Age</label>
        <select id="age">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
          <option>6</option>
          <option>7</option>
          <option>8</option>
          <option>9</option>
          <option>10</option>
        </select>
      </container>
      <container>
        <label for="freeText">Free Text</label>
        <input type="textarea" id="freeText" />
      </container>
      <button>Continue</button>
    </form>
  );
}

export default Form;
