import React from "react";
import { useState, useRef } from "react";
import "./App.css";
import styled from "styled-components";
const Img = styled.img`
  width: 400px;
`;
function App() {
  const image1Ref = useRef();
  const image2Ref = useRef();

  const image1 = require("./img/image1.jpg");
  const image1BlackWhite = require("./img/image1Bw.jpg");

  const image2 = require("./img/image2.jpg");
  const image2BlackWhite = require("./img/image2Bw.jpg");

  const changeColor = (e) => {
    const nameOf = e.target.name;
    console.log(nameOf);
    // target.src === image1
    //   ? (target.src = image2BlackWhite)
    //   : (target.src = image1BlackWhite);
  };

  return (
    <div>
      <Img
        onMouseOver={changeColor}
        ref={image1Ref}
        name="image1"
        src={image1}
      ></Img>
      <Img
        onMouseOver={changeColor}
        ref={image2Ref}
        name="image2"
        src={image2}
      ></Img>
    </div>
  );
}

export default App;
