import { useState } from "react";
import "./Counter.css";
function Counter() {
  let myColor = "black";
  let [currColor, setColor] = useState(myColor);
  let count = 0;
  let [num, setNum] = useState(count);
  const dec = function () {
    if (num > -10) {
      num--;
      setNum(num);
      if (num < 0) {
        setColor((previousState) => {
          return "red";
        });
      } else {
        setColor((previousState) => {
          return "black";
        });
      }
    }
  };

  const inc = function () {
    if (num < 10) {
      num++;
      setNum(num);
      if (num > 0)
        setColor((previousState) => {
          return "green";
        });
    } else {
      setColor((previousState) => {
        return "black";
      });
    }
  };

  return (
    <div className="counterBox">
      <button className="btn" onClick={dec}>
        -
      </button>
      <label style={{ color: currColor, fontWeight: "bold" }}>{num}</label>
      <button className="btn" onClick={inc}>
        +
      </button>
    </div>
  );
}
export default Counter;
