import { useState } from "react";
import "./form.css";
function Form() {
  const [text, setText] = useState("");
  const saveChangeHandler = function (event) {};
  console.log(text);

  return (
    <form>
      <container>
        <input
          type="text"
          id="firstName"
          onChange={(e) => setText(e.target.id)}
        />
        <label for="read">First Name</label>
      </container>
      <container>
        <input
          type="text"
          id="lastName"
          onChange={(e) => setText(e.target.id)}
        />
        <label for="accept">Last Name</label>
      </container>
      <container>
        <input
          type="number"
          id="want1"
          checked
          onChange={(e) => setText((prev) => e.target.id)}
        />
        <label for="want1">Age</label>
      </container>
      <container>
        <input
          type="checkbox"
          id="want2"
          checked
          onChange={(e) => setText(e.target.id)}
        />
        <label for="want2">I want2</label>
      </container>
    </form>
  );
}

export default Form;
