import React, { useState } from "react";
function App() {
  let x = "none";
  const toggleShow = function () {
    const [x, setX] = useState(x);
    setX((prevousState) => {
      return prevousState === "none" ? "block" : "none";
    });
  };
  return (
    <div>
      <button onClick={toggleShow}>Show / hide</button>
      <div style={{ display: { x } }}>box</div>
    </div>
  );
}

export default App;
