import { useState } from "react";

function Counter() {
  let count = 0;
  let [num, setNum] = useState(count);
  const dec = function () {
    if (previousState < -10) {
      setNum((previousState) => previousState - 1);
    }
  };

  const inc = function () {
    setNum((previousState) => previousState + 1);
  };

  return (
    <div>
      <button onClick={dec}>-</button>
      <label>{num}</label>
      <button onClick={inc}>+</button>
    </div>
  );
}
export default Counter;
